# Deploy verifying keys and volume registry
# Package ID: 0x3ff8ca9c96fd875fcdd810da4642d2c8d033b274df1fa9a84ccfa7fea3c1f927

$PACKAGE_ID = "0x3ff8ca9c96fd875fcdd810da4642d2c8d033b274df1fa9a84ccfa7fea3c1f927"

# Verifying keys (hex without 0x prefix for vector format)
$item_exists_vk = ""

$withdraw_vk = ""

$deposit_vk = ""

$transfer_vk = ""

$capacity_vk = ""

$deposit_capacity_vk = ""

$transfer_capacity_vk = ""

# Registry hash
$registry_hash = "e56eec604ad5592033b4138a4732ec87929bf11def8e957357922a02a2e1da22"

# Volumes: [0, 5, 3, 1, 10, 8, 2, 4, 3, 1, 1, 1, 6, 5, 2, 1]
$volumes = "[0, 5, 3, 1, 10, 8, 2, 4, 3, 1, 1, 1, 6, 5, 2, 1]"

Write-Host "Deploying verifying keys..."
Write-Host "Package ID: $PACKAGE_ID"
Write-Host ""

# Build the command
$cmd = @"
sui client ptb --gas-budget 200000000 --move-call "${PACKAGE_ID}::inventory::init_verifying_keys_and_share" "x${item_exists_vk}" "x${withdraw_vk}" "x${deposit_vk}" "x${transfer_vk}" "x${capacity_vk}" "x${deposit_capacity_vk}" "x${transfer_capacity_vk}"
"@

Write-Host "Executing command..."
Write-Host $cmd
Write-Host ""

Invoke-Expression $cmd
